/** @jsx jsx */
/* eslint-disable jsx-a11y/accessible-emoji */
import React, { useState, useEffect } from "react";
import { css, jsx } from '@emotion/core';
import { CSSTransition } from "react-transition-group";
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router';

const headerStyle = css`
  .Nav {
    position: fixed;
    top: 0; /* Stick it to the top */
    max-height: 70px;
    width: 100vw;

    display: grid;
    grid-template-areas: "logo nav";
  }

  .Logo {
    grid-area: logo;
    height: 70px;
  }

  .Nav {
    display: grid;
    grid-area: nav;
    grid-template-columns: repeat(4, auto);
    align-items: center;
    justify-items: center;
  }
  .Nav a {
    color: #fff;
    font-size: 20px;
    font-weight: 500;
    transition: 0.5s;
    text-decoration: none;
  }
  .Nav a:hover {
    transform: scale(1.1);
  }
  .Nav button {
    padding: 10px;
    outline: none;
    border: none;
    font-size: 20px;
    color: #fff;
    font-weight: 600;
    box-shadow: 0px 5px 0px 0px rgba(255, 0, 0, 0.25);
    border-radius: 10px;
    cursor: pointer;
    transition: 70ms;
  }

  .Burger {
    display: none;
    grid-area: burger;
    margin: 0 20px 0 0;
    padding: 0;
    justify-self: end;
    font-size: 40px;
    border: none;
    background: none;
    outline: none;
    transition: 0.1s;
  }
  .Burger:active {
    transform: scale(1.2);
  }

  @media (max-width: 700px) {
    .Nav {
      grid-template-areas: "logo burger" "nav nav";
    }
    .Nav {
      grid-template-rows: repeat(4, auto);
      grid-template-columns: none;
      grid-row-gap: 20px;

      padding: 30px 0 30px;
      background: rgba(40, 44, 47, 0.95);
      box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
      border-bottom-left-radius: 10px;
      border-bottom-right-radius: 10px;
    }
    .Burger {
      display: inline;
    }
  }

  .desktop-links {
    display: flex;
  }

  .mobile-links {
    display: inline-block;
  }

  .NavAnimation-enter {
    opacity: 0;
    transform: scale(0.5);
  }
  .NavAnimation-enter-active {
    opacity: 1;
    transform: translateX(0);
    transition: opacity 350ms, transform 350ms;
  }
  .NavAnimation-exit {
    opacity: 1;
  }
  .NavAnimation-exit-active {
    opacity: 0;
    transform: scale(0.5);
    transition: opacity 350ms, transform 350ms;
  }
`;

export default function Nav(props) {
  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  const renderLink = (link, path) => {
    const highlight = link.to !== '/' ? path.includes(link.to) : path === '/';
    return (
      <div key={link.text} className={'link' + (highlight ? ' Highlight' : '')}>
        <Link to={link.to}>{link.text}</Link>
      </div>
    );
  };

  const renderLinks = () => {
    const { links, pathname } = props;

    const parts = pathname.split('/');
    const path = '/' + parts[1] + (parts[2] ? '/' + parts[2] : '');

    return (
      <div className={`${isNavVisible ? 'desktop-links' : 'mobile-links'}`}>
        {links.map(link => {
          return renderLink(link, path);
        })}
      </div>
    );
  }

  useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 700px)");
    mediaQuery.addListener(handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  return (
    <div css={headerStyle}>
      <div className='Nav'>
        <h1>Voting Methods</h1>
        <CSSTransition
          in={!isSmallScreen || isNavVisible}
          timeout={350}
          className='NavAnimation'
          unmountOnExit
        >
          <div className={`${isNavVisible ? 'desktop-links' : 'mobile-links'}`}>
            {renderLinks()}
          </div>
        </CSSTransition>
        <button onClick={toggleNav} className='Burger'>
          ğŸ”
        </button>
      </div>
    </div>
  );
}
